 35→		bind:this={inputElement}
 36→		type="text"
 37→		value={query}
 38→		oninput={(e) => onchange((e.target as HTMLInputElement).value)}
 39→		placeholder="Search in document…"
 40→		class="input"
 41→	/>
 42→	<button
 43→		onclick={ontogglecasesensitive}
 44→		title="Case sensitive (⌘⌥C)"
 45→		class="toggle"
 46→		class:active={caseSensitive}
 47→	>
 48→		Aa
 49→	</button>
 50→	<button
 51→		onclick={ontogglewholeword}
 52→		title="Whole word (⌘⌥W)"
 53→		class="toggle"
 54→		class:active={wholeWord}
 55→	>
 56→		W
 57→	</button>
 58→	{#if query}
 59→		<span class="count">
 60→			{matchCount === 0
 61→				? "No matches"
 62→				: `${currentMatchIndex + 1} of ${matchCount}`}
 63→		</span>
 64→		<div class="nav-group">
 65→			<button
 66→				onclick={onprev}
 67→				disabled={!hasMatches}
 68→				title="Previous match (⌘⇧G)"
 69→				class="nav"
 70→				class:disabled={!hasMatches}
 71→			>
 72→				↑
 73→			</button>
 74→			<button
 75→				onclick={onnext}
 76→				disabled={!hasMatches}
 77→				title="Next match (⌘G)"
 78→				class="nav"
 79→				class:disabled={!hasMatches}
 80→			>
 81→				↓
 82→			</button>
 83→		</div>
 84→	{/if}
 85→</div>
