  1→<script lang="ts">
  2→	import { untrack } from "svelte";
  3→	import type { PDFiumDocument } from "@hyzyla/pdfium";
  4→	import type { PageData, SearchMatch } from "$lib/types";
  5→	import { findMatches } from "$lib/services/search";
  6→	import { splitPdf, downloadSplitPdfs } from "$lib/services/splitPdf";
  7→	import PdfPage from "./PdfPage.svelte";
  8→	import SearchBar from "./SearchBar.svelte";
  9→
 10→	interface Props {
 11→		pages: PageData[];
 12→		doc: PDFiumDocument;
 13→		splitMode: boolean;
 14→		pdfBytes: Uint8Array;
 15→	}
 16→
 17→	let { pages, doc, splitMode, pdfBytes }: Props = $props();
 18→
 19→	let query = $state("");
 20→	let debouncedQuery = $state("");
 21→	let caseSensitive = $state(false);
 22→	let wholeWord = $state(false);
 23→	let currentMatchIndex = $state(-1);
 24→	let splitPoints: Set<number> = $state(new Set());
 25→	let exporting = $state(false);
 26→	let thumbnailWidth = $state(180);
 27→
 28→	let searchInput: HTMLInputElement | undefined = $state();
 29→	let pageElements = new Map<number, HTMLDivElement>();
 30→
 31→	$effect(() => {
 32→		const q = query;
 33→		if (q === "") {
 34→			debouncedQuery = "";
 35→			return;
 36→		}
 37→		const timer = setTimeout(() => {
 38→			debouncedQuery = q;
 39→		}, 250);
 40→		return () => clearTimeout(timer);
 41→	});
 42→
 43→	let matches: SearchMatch[] = $derived(
 44→		findMatches(pages, debouncedQuery, { caseSensitive, wholeWord }),
 45→	);
 46→
 47→	// Reset to first match only when search parameters change (not when background extraction adds chars)
 48→	$effect(() => {
 49→		debouncedQuery;
 50→		caseSensitive;
 51→		wholeWord;
 52→		currentMatchIndex = untrack(() => matches.length) > 0 ? 0 : -1;
 53→	});
 54→
 55→	// Scroll active match into view
 56→	$effect(() => {
 57→		if (currentMatchIndex < 0 || matches.length === 0) return;
 58→		const match = matches[currentMatchIndex];
 59→		const pageEl = pageElements.get(match.pageIndex);
 60→		if (!pageEl) return;
 61→
 62→		const page = pages.find((p) => p.index === match.pageIndex);
 63→		if (!page) return;
 64→		const char = page.chars[match.charIndex];
 65→		if (!char) {
 66→			pageEl.scrollIntoView({ behavior: "smooth", block: "nearest" });
 67→			return;
 68→		}
 69→		const charYInPage = (page.originalHeight - char.top) * page.scale;
 70→		const pageRect = pageEl.getBoundingClientRect();
 71→		const targetY =
 72→			window.scrollY +
 73→			pageRect.top +
 74→			charYInPage -
 75→			window.innerHeight / 2;
 76→		window.scrollTo({ top: targetY, behavior: "smooth" });
 77→	});
 78→
 79→	// Clear split points when exiting split mode
 80→	$effect(() => {
 81→		if (!splitMode) {
 82→			splitPoints = new Set();
 83→		}
 84→	});
 85→
 86→	function toggleSplitPoint(pageIndex: number) {
 87→		const next = new Set(splitPoints);
 88→		if (next.has(pageIndex)) {
 89→			next.delete(pageIndex);
 90→		} else {
 91→			next.add(pageIndex);
 92→		}
 93→		splitPoints = next;
 94→	}
 95→
 96→	async function handleExport() {
 97→		if (splitPoints.size === 0 || exporting) return;
 98→		exporting = true;
 99→		try {
100→			const segments = await splitPdf(pdfBytes, [...splitPoints]);
101→			downloadSplitPdfs(segments);
102→		} finally {
103→			exporting = false;
104→		}
105→	}
106→
107→	function goToNext() {
108→		const n = matches.length;
109→		if (n === 0) return;
110→		currentMatchIndex = (currentMatchIndex + 1) % n;
111→	}
112→
113→	function goToPrev() {
114→		const n = matches.length;
115→		if (n === 0) return;
116→		currentMatchIndex = (currentMatchIndex - 1 + n) % n;
117→	}
118→
119→	function handleKeyDown(e: KeyboardEvent) {
120→		if (e.metaKey && !e.altKey && !e.shiftKey && e.code === "KeyF") {
121→			e.preventDefault();
122→			searchInput?.focus();
123→			searchInput?.select();
124→			return;
125→		}
126→
127→		if (e.metaKey && !e.altKey && e.code === "KeyG") {
128→			e.preventDefault();
129→			const n = matches.length;
130→			if (n === 0) return;
131→			if (e.shiftKey) {
132→				currentMatchIndex = (currentMatchIndex - 1 + n) % n;
133→			} else {
134→				currentMatchIndex = (currentMatchIndex + 1) % n;
135→			}
136→			return;
137→		}
138→
139→		if (!e.metaKey || !e.altKey) return;
140→		if (e.code === "KeyW") {
141→			e.preventDefault();
142→			wholeWord = !wholeWord;
143→		} else if (e.code === "KeyC") {
144→			e.preventDefault();
145→			caseSensitive = !caseSensitive;
146→		}
147→	}
148→
149→	function trackPageRef(node: HTMLDivElement, pageIndex: number) {
150→		pageElements.set(pageIndex, node);
151→		return {
152→			destroy() {
153→				pageElements.delete(pageIndex);
154→			},
155→		};
156→	}
157→</script>
158→
159→<svelte:window onkeydown={handleKeyDown} />
160→
161→<div>
162→	<SearchBar
163→		bind:inputElement={searchInput}
164→		{query}
165→		onchange={(q) => {
166→			query = q;
167→		}}
168→		matchCount={matches.length}
169→		{currentMatchIndex}
170→		onnext={goToNext}
171→		onprev={goToPrev}
172→		{caseSensitive}
173→		{wholeWord}
174→		ontogglecasesensitive={() => {
175→			caseSensitive = !caseSensitive;
176→		}}
177→		ontogglewholeword={() => {
178→			wholeWord = !wholeWord;
179→		}}
180→	/>
181→
182→	{#if splitMode}
183→		<div class="split-toolbar">
184→			<label class="scale-control">
185→				Size
186→				<input
187→					type="range"
188→					min="100"
189→					max="400"
190→					step="10"
191→					bind:value={thumbnailWidth}
192→				/>
193→			</label>
194→			{#if splitPoints.size > 0}
195→				<span class="split-info">
196→					{splitPoints.size} split point{splitPoints.size > 1 ? "s" : ""} selected
197→					&rarr; {splitPoints.size + 1} files
198→				</span>
199→				<button class="export-button" onclick={handleExport} disabled={exporting}>
200→					{exporting ? "Exporting…" : "Export Split PDFs"}
201→				</button>
202→			{/if}
203→		</div>
204→	{/if}
205→
206→	<div class="pages-container" class:split-grid={splitMode} style="--thumb-width: {thumbnailWidth}px">
207→		{#each pages as page, i (page.index)}
208→			{@const pageMatches = matches.filter(
209→				(m) => m.pageIndex === page.index,
210→			)}
211→			{@const activeCharIndex =
212→				currentMatchIndex >= 0 &&
213→				matches[currentMatchIndex]?.pageIndex === page.index
214→					? matches[currentMatchIndex].charIndex
215→					: -1}
216→			<div class="page-cell" use:trackPageRef={page.index}>
217→				{#if splitMode}
218→					<div class="page-number">Page {page.index + 1}</div>
219→				{/if}
220→				<div
221→					class="page-scale-wrapper"
222→					class:thumbnail={splitMode}
223→					style="--page-aspect-padding: {(page.height / page.width) * 100}%"
224→				>
225→					<PdfPage {page} matches={pageMatches} {activeCharIndex} {doc} />
226→				</div>
227→			</div>
228→			{#if splitMode && i < pages.length - 1}
229→				<button
230→					class="split-divider"
231→					class:split-active={splitPoints.has(page.index)}
232→					onclick={() => toggleSplitPoint(page.index)}
233→					title="Split after page {page.index + 1}"
234→				>
235→					<span class="split-divider-line"></span>
236→					<span class="split-label" class:split-label-active={splitPoints.has(page.index)}>&#9986;</span>
237→				</button>
238→			{/if}
239→		{/each}
240→	</div>
241→</div>
242→
243→<style>
244→	.pages-container {
245→		display: flex;
246→		flex-direction: column;
247→		align-items: center;
248→		gap: 16px;
249→		padding: 16px;
250→		overflow-x: auto;
251→	}
252→	.pages-container.split-grid {
