 1→/* Components: Chips */
 2→
 3→@utility chip {
 4→	border-radius: var(--radius-base);
 5→
 6→	/* Icon Spacing */
 7→	display: inline-flex;
 8→	flex-direction: row;
 9→	align-items: center;
10→	justify-content: center;
11→	gap: --spacing(2);
12→
13→	/* Typography */
14→	text-decoration-line: none;
15→	white-space: nowrap;
16→
17→	/* Size */
18→	font-size: var(--text-xs);
19→	line-height: var(--text-xs--line-height);
20→	padding-block: --spacing(1);
21→	padding-inline: --spacing(3);
22→
23→	/* Transitions */
24→	transition-property: all;
25→	transition-timing-function: var(--default-transition-timing-function);
26→	transition-duration: var(--default-transition-duration);
27→
28→	@variant hover {
29→		filter: brightness(125%);
30→		@variant dark {
31→			filter: brightness(75%);
32→		}
33→	}
34→}
35→
36→@utility chip-icon {
37→	box-sizing: content-box;
38→	border-radius: var(--radius-base);
39→
40→	/* Center Icons */
41→	display: inline-flex;
42→	justify-content: center;
43→	align-items: center;
44→
45→	/* Typography */
46→	text-decoration-line: none;
47→	white-space: nowrap;
48→
49→	/* Size */
50→	font-size: var(--text-xs);
51→	width: var(--text-xs);
52→	height: var(--text-xs);
53→	padding: --spacing(1.5);
54→
55→	@variant hover {
