  1→<script lang="ts">
  2→	import { onMount } from "svelte";
  3→	import { AppBar } from "@skeletonlabs/skeleton-svelte";
  4→	import type { PDFiumLibrary, PDFiumDocument } from "@hyzyla/pdfium";
  5→	import { getPdfiumLibrary } from "$lib/services/pdfium";
  6→	import { extractCharBoxes, RENDER_SCALE } from "$lib/services/charBoxes";
  7→	import type { PageData } from "$lib/types";
  8→	import PdfViewer from "$lib/components/PdfViewer.svelte";
  9→
 10→	let library: PDFiumLibrary | null = $state(null);
 11→	let libLoading = $state(true);
 12→	let libError: Error | null = $state(null);
 13→
 14→	let pages: PageData[] = $state([]);
 15→	let docLoading = $state(false);
 16→	let docError: Error | null = $state(null);
 17→	let currentDoc: PDFiumDocument | null = $state(null);
 18→	let charExtractionId = 0;
 19→	let pdfBytes: Uint8Array | null = $state(null);
 20→	let splitMode = $state(false);
 21→
 22→	onMount(() => {
 23→		getPdfiumLibrary()
 24→			.then((lib) => {
 25→				library = lib;
 26→			})
 27→			.catch((err: unknown) => {
 28→				libError = err instanceof Error ? err : new Error(String(err));
 29→			})
 30→			.finally(() => {
 31→				libLoading = false;
 32→			});
 33→	});
 34→
 35→	async function loadFile(file: File) {
 36→		if (!library) return;
 37→
 38→		docLoading = true;
 39→		docError = null;
 40→		pages = [];
 41→		splitMode = false;
 42→
 43→		currentDoc?.destroy();
 44→		currentDoc = null;
 45→
 46→		// Cancel any in-progress background char extraction
 47→		const extractionId = ++charExtractionId;
 48→
 49→		try {
 50→			const buffer = await file.arrayBuffer();
 51→			const uint8 = new Uint8Array(buffer);
 52→			pdfBytes = uint8;
 53→			const doc = await library.loadDocument(uint8);
 54→			currentDoc = doc;
 55→
 56→			// Phase 1: Build page metadata instantly (no render, no char extraction)
 57→			const pageCount = doc.getPageCount();
 58→			const pageDataList: PageData[] = [];
 59→
 60→			for (let i = 0; i < pageCount; i++) {
 61→				const page = doc.getPage(i);
 62→				const size = page.getSize(true);
 63→				pageDataList.push({
 64→					index: i,
 65→					originalWidth: size.width,
 66→					originalHeight: size.height,
 67→					width: Math.round(size.width * RENDER_SCALE),
 68→					height: Math.round(size.height * RENDER_SCALE),
 69→					chars: [],
 70→					scale: RENDER_SCALE,
 71→				});
 72→			}
 73→
 74→			pages = pageDataList;
 75→			docLoading = false;
 76→
 77→			// Phase 2: Background char extraction for search
 78→			for (let i = 0; i < pageCount; i++) {
 79→				if (extractionId !== charExtractionId) return;
 80→				const page = doc.getPage(i);
 81→				const chars = extractCharBoxes(page);
 82→				pages[i].chars = chars;
 83→				// Yield to main thread between pages
 84→				await new Promise((r) => setTimeout(r, 0));
 85→			}
 86→		} catch (err: unknown) {
 87→			docError = err instanceof Error ? err : new Error(String(err));
 88→			docLoading = false;
 89→		}
 90→	}
 91→
 92→	function handleFileChange(e: Event) {
 93→		const input = e.target as HTMLInputElement;
 94→		const file = input.files?.[0];
 95→		if (file) void loadFile(file);
 96→	}
 97→</script>
