110→			<AppBar.Toolbar>
111→				<AppBar.Lead>
112→					<h2 class="text-lg font-semibold">PDF Viewer</h2>
113→				</AppBar.Lead>
114→				<AppBar.Trail>
115→					<label class="btn preset-filled cursor-pointer">
116→						Open PDF
117→						<input
118→							type="file"
119→							accept=".pdf"
120→							onchange={handleFileChange}
121→							class="hidden"
122→						/>
123→					</label>
124→					{#if pages.length > 0}
125→						<button
126→							class="btn {splitMode ? 'preset-filled-error-500' : 'preset-filled'}"
127→							onclick={() => {
128→								splitMode = !splitMode;
129→							}}
130→						>
131→							{splitMode ? "Exit Split Mode" : "Split Mode"}
132→						</button>
133→					{/if}
134→					{#if docLoading}
135→						<span class="text-sm text-surface-500">Rendering pages…</span>
136→					{/if}
137→					{#if docError}
138→						<span class="text-sm text-error-500">Error: {docError.message}</span>
139→					{/if}
140→				</AppBar.Trail>
141→			</AppBar.Toolbar>
142→		</AppBar>
143→
144→		{#if pages.length > 0 && currentDoc && pdfBytes}
145→			<PdfViewer {pages} doc={currentDoc} {splitMode} {pdfBytes} />
146→		{:else if !docLoading}
147→			<div class="flex items-center justify-center h-[80vh] text-lg text-surface-500">
148→				Open a PDF file to get started.
149→			</div>
150→		{/if}
151→	</div>
152→{/if}
