  1→<script lang="ts">
  2→	import { onMount } from "svelte";
  3→	import type { PDFiumLibrary, PDFiumDocument } from "@hyzyla/pdfium";
  4→	import { getPdfiumLibrary } from "$lib/services/pdfium";
  5→	import { extractCharBoxes, RENDER_SCALE } from "$lib/services/charBoxes";
  6→	import type { PageData } from "$lib/types";
  7→	import PdfViewer from "$lib/components/PdfViewer.svelte";
  8→
  9→	let library: PDFiumLibrary | null = $state(null);
 10→	let libLoading = $state(true);
 11→	let libError: Error | null = $state(null);
 12→
 13→	let pages: PageData[] = $state([]);
 14→	let docLoading = $state(false);
 15→	let docError: Error | null = $state(null);
 16→	let currentDoc: PDFiumDocument | null = $state(null);
 17→	let charExtractionId = 0;
 18→	let pdfBytes: Uint8Array | null = $state(null);
 19→	let splitMode = $state(false);
 20→
 21→	onMount(() => {
 22→		getPdfiumLibrary()
 23→			.then((lib) => {
 24→				library = lib;
 25→			})
 26→			.catch((err: unknown) => {
 27→				libError = err instanceof Error ? err : new Error(String(err));
 28→			})
 29→			.finally(() => {
 30→				libLoading = false;
 31→			});
 32→	});
 33→
 34→	async function loadFile(file: File) {
 35→		if (!library) return;
 36→
 37→		docLoading = true;
 38→		docError = null;
 39→		pages = [];
 40→		splitMode = false;
 41→
 42→		currentDoc?.destroy();
 43→		currentDoc = null;
 44→
 45→		// Cancel any in-progress background char extraction
 46→		const extractionId = ++charExtractionId;
 47→
 48→		try {
 49→			const buffer = await file.arrayBuffer();
 50→			const uint8 = new Uint8Array(buffer);
 51→			pdfBytes = uint8;
 52→			const doc = await library.loadDocument(uint8);
 53→			currentDoc = doc;
 54→
 55→			// Phase 1: Build page metadata instantly (no render, no char extraction)
 56→			const pageCount = doc.getPageCount();
 57→			const pageDataList: PageData[] = [];
 58→
 59→			for (let i = 0; i < pageCount; i++) {
 60→				const page = doc.getPage(i);
 61→				const size = page.getSize(true);
 62→				pageDataList.push({
 63→					index: i,
 64→					originalWidth: size.width,
 65→					originalHeight: size.height,
 66→					width: Math.round(size.width * RENDER_SCALE),
 67→					height: Math.round(size.height * RENDER_SCALE),
 68→					chars: [],
 69→					scale: RENDER_SCALE,
 70→				});
 71→			}
 72→
 73→			pages = pageDataList;
 74→			docLoading = false;
 75→
 76→			// Phase 2: Background char extraction for search
 77→			for (let i = 0; i < pageCount; i++) {
 78→				if (extractionId !== charExtractionId) return;
 79→				const page = doc.getPage(i);
 80→				const chars = extractCharBoxes(page);
 81→				pages[i].chars = chars;
 82→				// Yield to main thread between pages
 83→				await new Promise((r) => setTimeout(r, 0));
 84→			}
 85→		} catch (err: unknown) {
 86→			docError = err instanceof Error ? err : new Error(String(err));
 87→			docLoading = false;
 88→		}
 89→	}
 90→
 91→	function handleFileChange(e: Event) {
 92→		const input = e.target as HTMLInputElement;
 93→		const file = input.files?.[0];
 94→		if (file) void loadFile(file);
 95→	}
 96→</script>
 97→
 98→{#if libLoading}
 99→	<div class="center">Loading PDFium Wasm…</div>
100→{:else if libError}
101→	<div class="center">Failed to load PDFium: {libError.message}</div>
102→{:else}
103→	<div>
104→		<div class="toolbar">
105→			<h2 class="title">PDF Viewer</h2>
106→			<label class="button">
107→				Open PDF
108→				<input
109→					type="file"
110→					accept=".pdf"
111→					onchange={handleFileChange}
112→					style="display:none"
113→				/>
114→			</label>
115→			{#if pages.length > 0}
116→				<button
117→					class="button"
118→					class:button-active={splitMode}
119→					onclick={() => {
120→						splitMode = !splitMode;
121→					}}
122→				>
123→					{splitMode ? "Exit Split Mode" : "Split Mode"}
124→				</button>
125→			{/if}
126→			{#if docLoading}
127→				<span class="status">Rendering pages…</span>
128→			{/if}
129→			{#if docError}
130→				<span class="error-text">Error: {docError.message}</span>
131→			{/if}
132→		</div>
133→
134→		{#if pages.length > 0 && currentDoc && pdfBytes}
135→			<PdfViewer {pages} doc={currentDoc} {splitMode} {pdfBytes} />
136→		{:else if !docLoading}
137→			<div class="center">Open a PDF file to get started.</div>
138→		{/if}
139→	</div>
140→{/if}
141→
