160→		margin: 0;
161→		font-size: 18px;
162→		font-weight: 600;
163→	}
164→	.button {
165→		padding: 6px 14px;
166→		background: #4f6ef7;
167→		color: #fff;
168→		border: none;
169→		border-radius: 4px;
170→		cursor: pointer;
171→		font-size: 14px;
172→		user-select: none;
173→	}
174→	.button-active {
175→		background: #e74c3c;
176→	}
177→	.status {
178→		font-size: 13px;
179→		color: #555;
180→	}
181→	.error-text {
182→		font-size: 13px;
183→		color: #c00;
184→	}
185→</style>
