  1→<script lang="ts">
  2→	interface Props {
  3→		inputElement?: HTMLInputElement;
  4→		query: string;
  5→		onchange: (q: string) => void;
  6→		matchCount: number;
  7→		currentMatchIndex: number;
  8→		onnext: () => void;
  9→		onprev: () => void;
 10→		caseSensitive: boolean;
 11→		wholeWord: boolean;
 12→		ontogglecasesensitive: () => void;
 13→		ontogglewholeword: () => void;
 14→	}
 15→
 16→	let {
 17→		inputElement = $bindable(),
 18→		query,
 19→		onchange,
 20→		matchCount,
 21→		currentMatchIndex,
 22→		onnext,
 23→		onprev,
 24→		caseSensitive,
 25→		wholeWord,
 26→		ontogglecasesensitive,
 27→		ontogglewholeword,
 28→	}: Props = $props();
 29→
 30→	let hasMatches = $derived(matchCount > 0);
 31→</script>
 32→
