285→		padding-bottom: var(--page-aspect-padding);
286→		overflow: hidden;
287→	}
288→	.page-scale-wrapper.thumbnail :global(canvas) {
289→		position: absolute;
290→		top: 0;
291→		left: 0;
292→		width: 100% !important;
293→		height: 100% !important;
294→	}
295→	.split-divider {
296→		display: flex;
297→		flex-direction: column;
298→		align-items: center;
299→		justify-content: center;
300→		width: 36px;
301→		border: none;
302→		background: transparent;
303→		cursor: pointer;
304→		padding: 0;
305→		position: relative;
306→	}
307→	.split-divider-line {
