  1→/* Components: Forms > Input Group */
  2→
  3→@utility input-group {
  4→	border-radius: var(--radius-base);
  5→	display: grid;
  6→	align-items: stretch;
  7→	overflow: hidden;
  8→
  9→	/* Edges */
 10→	outline-color: transparent;
 11→	border-width: 0;
 12→	--tw-ring-inset: inset;
 13→	--tw-ring-color: var(--color-surface-200-800);
 14→	--tw-ring-shadow: var(--tw-ring-inset,) 0 0 0 var(--default-ring-width) var(--tw-ring-color, currentColor);
 15→	box-shadow: var(--tw-inset-shadow), var(--tw-inset-ring-shadow), var(--tw-ring-offset-shadow), var(--tw-ring-shadow), var(--tw-shadow);
 16→
 17→	/* Child Edges */
 18→	& > :first-child {
 19→		border-top-left-radius: var(--radius-base);
 20→		border-bottom-left-radius: var(--radius-base);
 21→	}
 22→	& > :last-child {
 23→		border-top-right-radius: var(--radius-base);
 24→		border-bottom-right-radius: var(--radius-base);
 25→	}
 26→
 27→	/* Dividers */
 28→	& > * + * {
 29→		border-left-width: 1px !important;
 30→		border-color: var(--color-surface-200-800);
 31→	}
 32→}
 33→
 34→/* Children: Cell --- */
 35→
 36→@utility ig-cell {
 37→	font-size: var(--text-sm);
 38→	display: flex;
 39→	justify-content: center;
 40→	align-items: center;
 41→	padding-inline: --spacing(4);
 42→}
 43→
 44→/* Children: Form Elements --- */
 45→/* We recreate a subset of form element styles here */
 46→/* These are purpose-built to work in the group */
 47→
 48→@utility ig-input {
 49→	background-color: transparent;
 50→	display: block;
 51→	width: 100%;
 52→
 53→	/* Size */
 54→	font-size: var(--text-base);
 55→	line-height: var(--text-base--line-height);
 56→	padding-block: --spacing(1);
 57→	padding-inline: --spacing(3);
 58→
 59→	/* Edges */
 60→	outline-color: transparent;
 61→	border-width: 0;
 62→	--tw-ring-inset: inset;
 63→
 64→	@variant active {
 65→		--tw-ring-color: var(--color-primary-500);
 66→	}
 67→	@variant focus {
 68→		--tw-ring-color: var(--color-primary-500);
 69→	}
 70→	@variant focus-within {
 71→		--tw-ring-color: var(--color-primary-500);
 72→	}
 73→}
 74→
 75→@utility ig-select {
 76→	background-color: transparent;
 77→	display: block;
 78→	width: 100%;
 79→
 80→	/* Size */
 81→	font-size: var(--text-base);
 82→	line-height: var(--text-base--line-height);
