content.content:
  1→<script lang="ts">
  2→	import { onMount } from "svelte";
  3→	import type { PDFiumLibrary, PDFiumDocument } from "@hyzyla/pdfium";
  4→	import { getPdfiumLibrary } from "./services/pdfium";
  5→	import { extractCharBoxes, RENDER_SCALE } from "./services/charBoxes";
  6→	import type { PageData } from "./types";
  7→	import PdfViewer from "./components/PdfViewer.svelte";
  8→
  9→	let library: PDFiumLibrary | null = $state(null);
 10→	let libLoading = $state(true);
 11→	let libError: Error | null = $state(null);
 12→
 13→	let pages: PageData[] = $state([]);
 14→	let docLoading = $state(false);
 15→	let docError: Error | null = $state(null);
 16→	let currentDoc: PDFiumDocument | null = null;
 17→
 18→	onMount(() => {
 19→		getPdfiumLibrary()
 20→			.then((lib) => {
 21→				library = lib;
 22→			})
 23→			.catch((err: unknown) => {
 24→				libError = err instanceof Error ? err : new Error(String(err));
 25→			})
 26→			.finally(() => {
 27→				libLoading = false;
 28→			});
 29→	});
 30→
 31→	async function loadFile(file: File) {
 32→		if (!library) return;
 33→
 34→		docLoading = true;
 35→		docError = null;
 36→		pages = [];
 37→
 38→		currentDoc?.destroy();
 39→		currentDoc = null;
 40→
 41→		try {
 42→			const buffer = await file.arrayBuffer();
 43→			const uint8 = new Uint8Array(buffer);
 44→			const doc = await library.loadDocument(uint8);
 45→			currentDoc = doc;
 46→
 47→			const pageDataList: PageData[] = [];
 48→
 49→			for (const page of doc.pages()) {
 50→				const chars = extractCharBoxes(page);
 51→				const rendered = await page.render({
 52→					scale: RENDER_SCALE,
 53→					render: "bitmap",
 54→				});
 55→
 56→				const imageData = new ImageData(
 57→					new Uint8ClampedArray(rendered.data.buffer),
 58→					rendered.width,
 59→					rendered.height,
 60→				);
 61→
 62→				pageDataList.push({
 63→					index: page.number,
 64→					imageData,
 65→					width: rendered.width,
 66→					height: rendered.height,
 67→					originalWidth: rendered.originalWidth,
 68→					originalHeight: rendered.originalHeight,
 69→					chars,
 70→					scale: RENDER_SCALE,
 71→				});
 72→			}
 73→
 74→			pages = pageDataList;
 75→		} catch (err: unknown) {
 76→			docError = err instanceof Error ? err : new Error(String(err));
 77→		} finally {
 78→			docLoading = false;
 79→		}
 80→	}
 81→
 82→	function handleFileChange(e: Event) {
 83→		const input = e.target as HTMLInputElement;
 84→		const file = input.files?.[0];
 85→		if (file) void loadFile(file);
 86→	}
 87→</script>
 88→
 89→{#if libLoading}
 90→	<div class="center">Loading PDFium Wasm…</div>
 91→{:else if libError}
 92→	<div class="center">Failed to load PDFium: {libError.message}</div>
 93→{:else}
 94→	<div>
 95→		<div class="toolbar">
 96→			<h2 class="title">PDF Viewer</h2>
 97→			<label class="button">
 98→				Open PDF
 99→				<input
100→					type="file"
101→					accept=".pdf"
102→					onchange={handleFileChange}
103→					style="display:none"
104→				/>
105→			</label>
106→			{#if docLoading}
107→				<span class="status">Rendering pages…</span>
108→			{/if}
109→			{#if docError}
110→				<span class="error-text">Error: {docError.message}</span>
111→			{/if}
112→		</div>
113→
114→		{#if pages.length > 0}
115→			<PdfViewer {pages} />
116→		{:else if !docLoading}
117→			<div class="center">Open a PDF file to get started.</div>
118→		{/if}
119→	</div>
120→{/if}
121→
