123→	.center {
124→		display: flex;
125→		justify-content: center;
126→		align-items: center;
127→		height: 80vh;
128→		font-size: 18px;
129→		color: #555;
130→	}
131→	.toolbar {
132→		display: flex;
133→		align-items: center;
134→		gap: 16px;
135→		padding: 10px 16px;
136→		background: #f5f5f5;
137→		border-bottom: 1px solid #ddd;
138→	}
139→	.title {
140→		margin: 0;
141→		font-size: 18px;
142→		font-weight: 600;
143→	}
144→	.button {
145→		padding: 6px 14px;
146→		background: #4f6ef7;
147→		color: #fff;
148→		border-radius: 4px;
149→		cursor: pointer;
150→		font-size: 14px;
151→		user-select: none;
152→	}
153→	.status {
154→		font-size: 13px;
155→		color: #555;
156→	}
157→	.error-text {
158→		font-size: 13px;
159→		color: #c00;
160→	}
161→</style>
