file.content:
/** Per-character bounding box in PDF coordinate space (origin bottom-left, Y-up, units: points) */
export interface CharBox {
	char: string;
	left: number;
	right: number;
	bottom: number;
	top: number;
}

/** Rendered page data — holds the bitmap and all character positions */
export interface PageData {
	index: number;
	imageData: ImageData;
	/** Rendered width in pixels */
	width: number;
	/** Rendered height in pixels */
	height: number;
	/** Page width in PDF points (used for coordinate conversion) */
	originalWidth: number;
	/** Page height in PDF points (used for Y-flip: y = (originalHeight - top) * scale) */
	originalHeight: number;
	chars: CharBox[];
	scale: number;
}

/** One search match — identifies a run of characters on a page */
export interface SearchMatch {
	pageIndex: number;
	charIndex: number;
	charCount: number;
}

export interface SearchOptions {
	caseSensitive: boolean;
	wholeWord: boolean;
}

content.content:
  1→<script lang="ts">
  2→	import type { PageData, SearchMatch } from "../types";
  3→	import { findMatches } from "../services/search";
  4→	import PdfPage from "./PdfPage.svelte";
  5→	import SearchBar from "./SearchBar.svelte";
  6→
  7→	interface Props {
  8→		pages: PageData[];
  9→	}
 10→
 11→	let { pages }: Props = $props();
 12→
 13→	let query = $state("");
 14→	let debouncedQuery = $state("");
 15→	let caseSensitive = $state(false);
 16→	let wholeWord = $state(false);
 17→	let currentMatchIndex = $state(-1);
 18→
 19→	let searchInput: HTMLInputElement | undefined = $state();
 20→	let pageElements = new Map<number, HTMLDivElement>();
 21→
 22→	$effect(() => {
 23→		const q = query;
 24→		if (q === "") {
 25→			debouncedQuery = "";
 26→			return;
 27→		}
 28→		const timer = setTimeout(() => {
 29→			debouncedQuery = q;
 30→		}, 250);
 31→		return () => clearTimeout(timer);
 32→	});
 33→
 34→	let matches: SearchMatch[] = $derived(
 35→		findMatches(pages, debouncedQuery, { caseSensitive, wholeWord }),
 36→	);
 37→
 38→	// Reset to first match when results change
 39→	$effect(() => {
 40→		// Access reactive deps
 41→		matches.length;
 42→		debouncedQuery;
 43→		caseSensitive;
 44→		wholeWord;
 45→		currentMatchIndex = matches.length > 0 ? 0 : -1;
 46→	});
 47→
 48→	// Scroll active match into view
 49→	$effect(() => {
 50→		if (currentMatchIndex < 0 || matches.length === 0) return;
 51→		const match = matches[currentMatchIndex];
 52→		const pageEl = pageElements.get(match.pageIndex);
 53→		if (!pageEl) return;
 54→
 55→		const page = pages.find((p) => p.index === match.pageIndex);
 56→		if (!page) return;
 57→		const char = page.chars[match.charIndex];
 58→		if (!char) {
 59→			pageEl.scrollIntoView({ behavior: "smooth", block: "nearest" });
 60→			return;
 61→		}
 62→		const charYInPage = (page.originalHeight - char.top) * page.scale;
 63→		const pageRect = pageEl.getBoundingClientRect();
 64→		const targetY =
 65→			window.scrollY +
 66→			pageRect.top +
 67→			charYInPage -
 68→			window.innerHeight / 2;
 69→		window.scrollTo({ top: targetY, behavior: "smooth" });
 70→	});
 71→
 72→	function goToNext() {
 73→		const n = matches.length;
 74→		if (n === 0) return;
 75→		currentMatchIndex = (currentMatchIndex + 1) % n;
 76→	}
 77→
 78→	function goToPrev() {
 79→		const n = matches.length;
 80→		if (n === 0) return;
 81→		currentMatchIndex = (currentMatchIndex - 1 + n) % n;
 82→	}
 83→
 84→	function handleKeyDown(e: KeyboardEvent) {
 85→		if (e.metaKey && !e.altKey && !e.shiftKey && e.code === "KeyF") {
 86→			e.preventDefault();
 87→			searchInput?.focus();
 88→			searchInput?.select();
 89→			return;
 90→		}
 91→
 92→		if (e.metaKey && !e.altKey && e.code === "KeyG") {
 93→			e.preventDefault();
 94→			const n = matches.length;
 95→			if (n === 0) return;
 96→			if (e.shiftKey) {
 97→				currentMatchIndex = (currentMatchIndex - 1 + n) % n;
 98→			} else {
 99→				currentMatchIndex = (currentMatchIndex + 1) % n;
100→			}
101→			return;
102→		}
103→
104→		if (!e.metaKey || !e.altKey) return;
105→		if (e.code === "KeyW") {
106→			e.preventDefault();
107→			wholeWord = !wholeWord;
108→		} else if (e.code === "KeyC") {
109→			e.preventDefault();
110→			caseSensitive = !caseSensitive;
111→		}
112→	}
113→
114→	function trackPageRef(node: HTMLDivElement, pageIndex: number) {
115→		pageElements.set(pageIndex, node);
116→		return {
117→			destroy() {
118→				pageElements.delete(pageIndex);
119→			},
120→		};
121→	}
122→</script>
123→
124→<svelte:window onkeydown={handleKeyDown} />
125→
126→<div>
127→	<SearchBar
128→		bind:inputElement={searchInput}
129→		{query}
130→		onchange={(q) => {
131→			query = q;
132→		}}
133→		matchCount={matches.length}
134→		{currentMatchIndex}
135→		onnext={goToNext}
136→		onprev={goToPrev}
137→		{caseSensitive}
138→		{wholeWord}
139→		ontogglecasesensitive={() => {
140→			caseSensitive = !caseSensitive;
141→		}}
142→		ontogglewholeword={() => {
143→			wholeWord = !wholeWord;
144→		}}
145→	/>
146→	<div class="pages-container">
147→		{#each pages as page (page.index)}
148→			{@const pageMatches = matches.filter(
149→				(m) => m.pageIndex === page.index,
150→			)}
151→			{@const activeCharIndex =
152→				currentMatchIndex >= 0 &&
153→				matches[currentMatchIndex]?.pageIndex === page.index
154→					? matches[currentMatchIndex].charIndex
155→					: -1}
156→			<div use:trackPageRef={page.index}>
157→				<PdfPage {page} matches={pageMatches} {activeCharIndex} />
158→			</div>
159→		{/each}
160→	</div>
161→</div>
162→
163→<style>
164→	.pages-container {
165→		display: flex;
166→		flex-direction: column;
167→		align-items: center;
168→		gap: 16px;
169→		padding: 16px;
170→		overflow-x: auto;
171→	}
172→</style>
173→

  1→<script lang="ts">
  2→	import type { PageData, SearchMatch } from "../types";
  3→
  4→	interface Props {
  5→		page: PageData;
  6→		matches: SearchMatch[];
  7→		activeCharIndex: number;
  8→	}
  9→
 10→	let { page, matches, activeCharIndex }: Props = $props();
 11→
 12→	let renderCanvas: HTMLCanvasElement;
 13→	let overlayCanvas: HTMLCanvasElement;
 14→
 15→	function pdfToCanvas(
 16→		left: number,
 17→		right: number,
 18→		bottom: number,
 19→		top: number,
 20→		pageHeightPt: number,
 21→		scale: number,
 22→	) {
 23→		return {
 24→			x: left * scale,
 25→			y: (pageHeightPt - top) * scale,
 26→			w: (right - left) * scale,
 27→			h: (top - bottom) * scale,
 28→		};
 29→	}
 30→
 31→	// Paint rendered bitmap
 32→	$effect(() => {
 33→		if (!renderCanvas) return;
 34→		const ctx = renderCanvas.getContext("2d");
 35→		if (!ctx) return;
 36→		renderCanvas.width = page.width;
 37→		renderCanvas.height = page.height;
 38→		ctx.putImageData(page.imageData, 0, 0);
 39→	});
 40→
 41→	// Draw search highlights
 42→	$effect(() => {
 43→		if (!overlayCanvas) return;
 44→		const ctx = overlayCanvas.getContext("2d");
 45→		if (!ctx) return;
 46→		overlayCanvas.width = page.width;
 47→		overlayCanvas.height = page.height;
 48→		ctx.clearRect(0, 0, overlayCanvas.width, overlayCanvas.height);
 49→
 50→		if (matches.length === 0) return;
 51→
 52→		function drawMatch(match: SearchMatch, color: string) {
 53→			ctx!.fillStyle = color;
 54→			for (
 55→				let i = match.charIndex;
 56→				i < match.charIndex + match.charCount;
 57→				i++
 58→			) {
 59→				const c = page.chars[i];
 60→				if (!c) continue;
 61→				const { x, y, w, h } = pdfToCanvas(
 62→					c.left,
 63→					c.right,
 64→					c.bottom,
 65→					c.top,
 66→					page.originalHeight,
 67→					page.scale,
 68→				);
 69→				if (w > 0 && h > 0) ctx!.fillRect(x, y, w, h);
 70→			}
 71→		}
 72→
 73→		for (const match of matches) {
 74→			if (match.charIndex !== activeCharIndex) {
 75→				drawMatch(match, "rgba(255, 220, 0, 0.45)");
 76→			}
 77→		}
 78→		if (activeCharIndex >= 0) {
 79→			const active = matches.find((m) => m.charIndex === activeCharIndex);
 80→			if (active) drawMatch(active, "rgba(255, 140, 0, 0.75)");
 81→		}
 82→	});
 83→</script>
 84→
 85→<div
 86→	class="page-wrapper"
 87→	style="width: {page.width}px; height: {page.height}px;"
 88→>
 89→	<canvas bind:this={renderCanvas} class="layer"></canvas>
 90→	<canvas bind:this={overlayCanvas} class="layer"></canvas>
 91→</div>
 92→
 93→<style>
 94→	.page-wrapper {
 95→		position: relative;
 96→		box-shadow: 0 2px 8px rgba(0, 0, 0, 0.25);
 97→	}
 98→	.layer {
 99→		position: absolute;
100→		top: 0;
101→		left: 0;
102→	}
103→</style>
104→

  1→<script lang="ts">
  2→	interface Props {
  3→		inputElement?: HTMLInputElement;
  4→		query: string;
  5→		onchange: (q: string) => void;
  6→		matchCount: number;
  7→		currentMatchIndex: number;
  8→		onnext: () => void;
  9→		onprev: () => void;
 10→		caseSensitive: boolean;
 11→		wholeWord: boolean;
 12→		ontogglecasesensitive: () => void;
 13→		ontogglewholeword: () => void;
 14→	}
 15→
 16→	let {
 17→		inputElement = $bindable(),
 18→		query,
 19→		onchange,
 20→		matchCount,
 21→		currentMatchIndex,
 22→		onnext,
 23→		onprev,
 24→		caseSensitive,
 25→		wholeWord,
 26→		ontogglecasesensitive,
 27→		ontogglewholeword,
 28→	}: Props = $props();
 29→
 30→	let hasMatches = $derived(matchCount > 0);
 31→</script>
 32→
 33→<div class="container">
 34→	<input
 35→		bind:this={inputElement}
 36→		type="text"
 37→		value={query}
 38→		oninput={(e) => onchange((e.target as HTMLInputElement).value)}
 39→		placeholder="Search in document…"
 40→		class="input"
 41→	/>
 42→	<button
 43→		onclick={ontogglecasesensitive}
 44→		title="Case sensitive (⌘⌥C)"
 45→		class="toggle"
 46→		class:active={caseSensitive}
 47→	>
 48→		Aa
 49→	</button>
 50→	<button
 51→		onclick={ontogglewholeword}
 52→		title="Whole word (⌘⌥W)"
 53→		class="toggle"
 54→		class:active={wholeWord}
 55→	>
 56→		W
 57→	</button>
 58→	{#if query}
 59→		<span class="count">
 60→			{matchCount === 0
 61→				? "No matches"
 62→				: `${currentMatchIndex + 1} of ${matchCount}`}
 63→		</span>
 64→		<div class="nav-group">
 65→			<button
 66→				onclick={onprev}
 67→				disabled={!hasMatches}
 68→				title="Previous match (⌘⇧G)"
 69→				class="nav"
 70→				class:disabled={!hasMatches}
 71→			>
 72→				↑
 73→			</button>
 74→			<button
 75→				onclick={onnext}
 76→				disabled={!hasMatches}
 77→				title="Next match (⌘G)"
 78→				class="nav"
 79→				class:disabled={!hasMatches}
 80→			>
 81→				↓
 82→			</button>
 83→		</div>
 84→	{/if}
 85→</div>
 86→
 87→<style>
 88→	.container {
 89→		position: sticky;
 90→		top: 0;
 91→		z-index: 100;
 92→		display: flex;
 93→		align-items: center;
 94→		gap: 4px;
 95→		padding: 10px 16px;
 96→		background: #fff;
